import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./OrderPage.css";

const OrderPage = () => {
  const [orderDetails, setOrderDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Dynamically load Razorpay script when the component is mounted
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  // Function to handle the ordering logic
  const handleOrder = async () => {
    setIsLoading(true);
    setError(null);

    try {
      // Sending a POST request to the API
      const response = await fetch(
        "http://localhost:9090/api/payment/create-order?productId=123",
        {
          method: "POST",
        }
      );

      if (!response.ok) {
        throw new Error("Failed to create order");
      }

      // Parse the response JSON
      const data = await response.json();
      console.log(data);
      // Update state with the order details
      setOrderDetails({
        orderId: data.orderId,
        amount: data.product.price,
      });
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePayment = () => {
    if (!orderDetails) return;

    const options = {
      key: "zp_test_HF2lHcv", // Enter your Razorpay Key ID
      amount: orderDetails.amount * 100, // Razorpay accepts amount in paise (1 INR = 100 paise)
      currency: "INR",
      name: "Nikhil",
      description: "Test Transaction",
      // image: "https://example.com/your_logo", // Replace with your logo URL
      order_id: orderDetails.orderId, // Dynamic Order ID
      handler: function (response) {
        // After success, navigate to success page
        navigate("/payment-success", {
          state: {
            paymentId: response.razorpay_payment_id,
            orderId: response.razorpay_order_id,
            signature: response.razorpay_signature,
          },
        });

        // Optionally, you can also call the API here
        fetch(
          `http://localhost:9090/api/payment/updatePaymentDetails?payment_id=${response.razorpay_payment_id}&order_id=${response.razorpay_order_id}&signature=${response.razorpay_signature}`,
          { method: "GET" }
        );
      },
      prefill: {
        name: "Nikhil",
        email: "nikhil@cosmostaker.com",
        contact: "9000090000",
      },
      notes: {
        address: "Razorpay Corporate Office",
      },
      theme: {
        color: "#3399cc",
      },
    };

    const rzp1 = new window.Razorpay(options); // Access Razorpay from window object
    rzp1.open();
  };

  return (
    <div className="order-container">
      <div className="order-box">
        <h1 className="order-title">Order Your Product</h1>

        <button
          className="order-button"
          onClick={handleOrder}
          disabled={isLoading}
        >
          {isLoading ? "Ordering..." : "Order Wireless Headphones"}
        </button>

        {error && <p className="error-message">{error}</p>}

        {orderDetails && (
          <div className="order-summary">
            <h3 className="order-summary-title">Order Summary</h3>
            <p>
              <strong>Order ID:</strong> {orderDetails.orderId}
            </p>
            <p>
              <strong>Amount:</strong> â‚¹{orderDetails.amount}
            </p>

            {/* Payment Button */}
            <button className="pay-button" onClick={handlePayment}>
              Pay Now with Razorpay
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderPage;
